/*	File:		ZLib.f	Project:	ZLib - NewtonScript shared lib*/DefineUnit('|ZLib:Kallisys|, {	GetZLibVersion:		ZLib.FZlibVersion,	ProtoDeflateStream:	{		DeflateInit:	ZLib.FDeflateInit,		DeflateInit2:	ZLib.FDeflateInit2,		Deflate:		ZLib.FDeflate,		DeflateEnd:		ZLib.FDeflateEnd,		DeflateReset:	ZLib.FDeflateReset	},	ProtoInflateStream:	{		InflateInit:	ZLib.FInflateInit,		InflateInit2:	ZLib.FInflateInit2,		Inflate:		ZLib.FInflate,		InflateEnd:		ZLib.FInflateEnd,		InflateReset:	ZLib.FInflateReset	}});SetPartFrameSlot('text, "ZLib");SetPartFrameSlot('tapAction, func()begin	GetRoot():Notify( 3, "ZLib " & kZLibVersionStr, "Shared library for compression/decompression.\n\nWritten by Jean-loup Gailly & Mark Adler and ported to NewtonOS by Paul Guyot." );end);// Cf MooUnit.f: they say we need InstallScript & RemoveScript.InstallScript := func(partFrame, removeFrame)beginend;RemoveScript := func(removeFrame)beginend;